import React, { ChangeEvent, useState } from 'react';

type FormState = {
  [key: string]: string;
};

const MovementForm: React.FC = () => {
  const [formData, setFormData] = useState<FormState>({});

  const handleMovementSubmit = (event: React.FormEvent<HTMLFormElement>)=>{
    event.preventDefault()
    console.log(formData)
    // const form = event.currentTarget
    // console.log()
  }

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    console.log(name,value)
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const addMovementForm =(
    <form className="pure-form" onSubmit={handleMovementSubmit}>
      <fieldset>
          <input placeholder="Movement Id" name="movement_id" onChange={handleChange}/>
          <input placeholder="Company" name="company" onChange={handleChange}/>
          <input placeholder="Origin Premise Id" name="origin_premise_id" onChange={handleChange}/>
          <input placeholder="Destination Premise Id" name="dest_premise_id" onChange={handleChange}/>
          <input placeholder="Species" name="species" onChange={handleChange}/>
          <input placeholder="Shipment Start Date" name="shipment_start_date" onChange={handleChange}/>
          <input placeholder="Reason" name="reason" onChange={handleChange}/>
          <button type="submit" className="pure-button pure-button-primary">Add</button>
      </fieldset>
    </form>
  );

  return (
    <div>
        {addMovementForm}
    </div>

  );
};

export default MovementForm;
