import React, {useState} from 'react';
import './App.css';
import internal from 'stream';
import MovementTable from './MovementTable';
import FarmForm from './FarmForm';

interface MyProps {
};

interface MyState {
  search: string;
  addFarm: boolean;
  addMovement: boolean;
};

type FormState = {
  [key: string]: string;
};

class App extends React.Component<MyProps,MyState>{
  constructor(props : MyProps){
    super(props);

    this.state = {
      search : "",
      addFarm: false,
      addMovement: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleFarmClick = this.handleFarmClick.bind(this);
    this.handleMovementClick = this.handleMovementClick.bind(this);
  }

  handleChange(event: React.FormEvent<HTMLInputElement>){
    const {name,value} = event.currentTarget;
    this.setState({

    })
  }

  handleFarmClick(){
    this.setState((state)=>({
      addFarm: !state.addFarm
    }));
  }

  handleMovementClick(){
    this.setState((state)=>({
      addMovement: !state.addMovement
    }));
  }

  render(): React.ReactNode {

    const addMovementForm =(
      <form className="pure-form">
        <fieldset>
            <input placeholder="Movement Id" />
            <input placeholder="Company" />
            <input placeholder="Origin Premise Id" />
            <input placeholder="Destination Premise Id" />
            <input placeholder="Species" />
            <input placeholder="Shipment Start Date" />
            <input placeholder="Reason" />
            <button type="submit" className="pure-button pure-button-primary">Add</button>
        </fieldset>
      </form>
    );

    return(
      <div>
        <div className="header">

          <div className="pure-menu pure-menu-horizontal">
            <a className="pure-menu-heading"></a>

            <ul className="pure-menu-list">
              <li className="pure-menu-item">
                <a href="#" className="pure-menu-link" onClick={this.handleFarmClick}>Add Farm</a>
              </li>
              <li className="pure-menu-item">
                <a href="#" className="pure-menu-link" onClick={this.handleMovementClick}>Add Movement</a>
              </li>
              <li className="pure-menu-item">
                <form className="pure-form">
                  <fieldset>
                  <input value={this.state.search} name="search" onChange={this.handleChange}/>
                  <button type="submit" className="pure-button pure-button-primary">Search</button>
                  </fieldset>
                </form>
              </li>
            </ul>
          </div>
        </div>
        <div>
          {this.state.addFarm ? <FarmForm/> : null}
          {this.state.addMovement ? addMovementForm : null}
          <h1>this.FormState.premise_id</h1>
        </div>
        <div>
          <MovementTable/>
        </div>
      </div>
    )
  }
}

export default App;
