import React from 'react';
import './App.css';
import internal from 'stream';
import MovementTable from './MovementTable';

interface MyProps {
};

interface MyState {
  input: string;
  addFarm: boolean;
  addMovement: boolean;
};

class App extends React.Component<MyProps,MyState>{
  constructor(props : MyProps){
    super(props);

    this.state = {
      input : "",
      addFarm: false,
      addMovement: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleFarmClick = this.handleFarmClick.bi(this);
  }

  handleChange(event: React.FormEvent<HTMLInputElement>){
    this.setState({
      input: event.currentTarget.value
    });
  }

  handleFarmClick(){
    this.setState({
      addFarm: true
    });
    console.log(this.state.addFarm)
  }

  // handleMovementClick(){
  //   this.setState((state)=>({
  //     addMovement: !state.addMovement
  //   }));
  // }

  render(): React.ReactNode {

    const addFarmForm = (
      <form className="pure-form">
        <fieldset>
            <legend>A compact inline form</legend>
            <input placeholder="Premise Id" />
            <input placeholder="Name" />
            <button type="submit" className="pure-button pure-button-primary">Add</button>
        </fieldset>
      </form>

    );

    return(
      <div className="header">

        <div className="pure-menu pure-menu-horizontal">
          <a className="pure-menu-heading"></a>

          <ul className="pure-menu-list">
            <li className="pure-menu-item">
              <a href="#" className="pure-menu-link" onClick={this.handleFarmClick}>Add Farm</a>
              {this.state.addFarm} ? {addFarmForm} : ;
            </li>
            <li className="pure-menu-item">
              <a href="#" className="pure-menu-link">Add Movement</a>
            </li>
            <li className="pure-menu-item">
              <form className="pure-form">
                <fieldset>
                <input value={this.state.input} onChange={this.handleChange}/>
                <button type="submit" className="pure-button pure-button-primary">Search</button>
                </fieldset>
              </form>
            </li>
          </ul>
        </div>
        <h1>{this.state.addFarm}</h1>
        <MovementTable/>
      </div>
    )
  }
}

export default App;
