import React from 'react';
import './App.css';

interface MyProps {
    isSearch:boolean
};

interface Movement{
  movement_id : number,
  company : string,
  origin_premise_id : string,
  dest_premise_id : string,
  species : string, 
  shipment_start_date : Date,
  items_moved : number,
  reason : string
}

interface MyState {
  status: string;
  data: Movement[];
};

class MovementTable extends React.Component<MyProps,MyState>{
  constructor(props : MyProps){
    super(props);
    this.state = {
      status: "Fetching records..",
      data: []
    };
  }

  async loadTableData(url:string){
    try {
        const res = await fetch(url);
        const temp = await res.json()

        this.setState({
          status : temp["status"],
          data: temp["data"]
        });

      } catch (e) {
        console.log(e);
      }
  }

  render(): React.ReactNode {

    if(this.props.isSearch){
        this.loadTableData("http://localhost:8000/movement/")
    }
    else{
        this.loadTableData("http://localhost:8000/movement/?premise_id=")
    }

    if(this.state.status=="error"){
      return(
        <h1>No data available</h1>
      )
    }
    else{
      return(
        <div>


        </div>
      )
    }
  }
}

export default MovementTable;
